(24, '[]', 'Tổng hợp nguyên liệu', 0, 0, '<p><b>Nguyên liệu làm bánh gồm:</b></p><ul><li><i><b>Tôm đất sống ở vùng cửa sông</b></i></li><li><i><b>Thịt ba chỉ thái miếng</b></i></li><li><i><b>Bột lọc tươi</b></i></li><li><i><b>Măng rừng</b></i></li><li><i><b>Mộc nhĩ đen</b></i></li><li><i><b>Hành tím ta</b></i></li><li><i><b>Lá chuối</b></i></li></ul>', '', 'Nguyên liệu làm bánh lọc Quảng Bình', 'uploads/20210824104340.jpg', 'uploads/thumbnail/20210824104340.jpg', 0, 0, 0, 25, 1629776622, 1, 0),
(25, '[]', 'Bánh lọc Quảng Bình', 0, 0, '<p><b>Bánh lọc Phương Thu:</b></p><ul><li><i>Bánh sống gói lá chuối</i></li><li><i>Bánh chín gói lá chuối</i></li><li><i>Bánh sống (để chiên)</i></li></ul>', NULL, 'Bánh lọc Phương Thu', 'uploads/20200911115555.jpg', 'uploads/thumbnail/20200911115555.jpg', 0, 0, 0, 47, 1599800292, 6, 0),
(26, '[]', 'Quy cách đóng gói', 80000, 0, '<p><b>Quy cách đóng gói:</b></p><ul><li><i><b>20 chiếc/ gói</b></i></li><li><i><b>Có nước chấm đặc trưng kèm theo mỗi gói</b></i></li><li><i><b>Giá 80k/gói bánh sống</b></i></li><li><i><b>Giá 90k/gói bánh chín, hấp nóng ăn ngay</b></i></li></ul>', '', 'Đóng gói bánh lọc Phương Thu', 'uploads/20210824104356.jpg', 'uploads/thumbnail/20210824104356.jpg', 0, 0, 0, 24, 1629776638, 1, 0),
